INSERT INTO `sh_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(1189, 144, 'single_tour_custom_fields_itineraries_day_7_description', '<span style=\"font-weight: 400;\">Today we’re on to our next destination, the truly delightful city of Pushkar.</span><span style=\"font-weight: 400;\">\r\n</span><span style=\"font-weight: 400;\">Located at the edge of the Rajasthan desert, this oasis is a small and very holy city with a relaxed hippy vibe and filled with incredible</span> <span style=\"font-weight: 400;\">markets! Pushkar offers your fill of temples and shopping while surrounded by natural beauty. As by now you’ll have done a fair bit of traveling, we’re giving you today to rest up and recharge. Relax by the hotel pool or head to explore under your own steam. Whatever you choose to do your guide will always be around to help out with ideas and advice.</span><span style=\"font-weight: 400;\">\r\n</span><span style=\"font-weight: 400;\">MEALS INCLUDED:</span> <span style=\"font-weight: 400;\">Breakfast</span>'),
