(2348, 138, '_single_tour_custom_fields_itineraries_day_10_headline', 'field_5de87cdd61911'),
(2349, 138, 'single_tour_custom_fields_itineraries_day_10_short_headline', 'Relaxing Koh Samui island tour, with lunch'),
(2350, 138, '_single_tour_custom_fields_itineraries_day_10_short_headline', 'field_5de87cdd61912'),
(2351, 138, 'single_tour_custom_fields_itineraries_day_10_description', '<span style=\"font-weight: 400;\">This morning it’s time to depart the Sanctuary and head back to our hotel to regroup and for some serious rest and relaxation. By now you’ll be feeling on cloud nine and really ready to make the most of the next few days. However, today we’ve been advised that you should take it easy so that’s what we’ll do, probably around the pool with a cool glass of wine or a cocktail!</span>\r\n\r\n<b>MEALS INCLUDED:</b> <span style=\"font-weight: 400;\">Breakfast</span>'),
