(2371, 138, 'single_tour_custom_fields_itineraries_day_12_headline', ' Koh Samui, Thailand'),
(2372, 138, '_single_tour_custom_fields_itineraries_day_12_headline', 'field_5def2e48d54d7'),
(2373, 138, 'single_tour_custom_fields_itineraries_day_12_short_headline', 'Direct flight back to bangkok and transfer to hotel with final night group dinner'),
(2374, 138, '_single_tour_custom_fields_itineraries_day_12_short_headline', 'field_5def2e48d54d8'),
(2375, 138, 'single_tour_custom_fields_itineraries_day_12_description', '<span style=\"font-weight: 400;\">Sadly, this morning it’s time to leave Koh Samui behind and take the 60-minute flight back to Bangkok. On arrival, we’ll transfer to a stunning hotel for one final night. You’re invited to take advantage of the excellent facilities here, from the huge swimming pool to the clay tennis courts, well-created gym and lots more.</span><span style=\"font-weight: 400;\">\r\n</span><span style=\"font-weight: 400;\">Again, as tonight is the final night of the tour, we’ll organize a final night dinner to fabulously celebrate the end of incredible time on a life-changing tour together.</span>\r\n\r\n<b>MEALS INCLUDED:</b> <span style=\"font-weight: 400;\">Breakfast, dinner</span>'),
